DROP DATABASE IF EXISTS frandiab_dwes;
CREATE DATABASE IF NOT EXISTS frandiab_dwes DEFAULT CHARACTER SET latin1 COLLATE latin1_spanish_ci;
USE frandiab_dwes;

DROP TABLE IF EXISTS ORDERLINES;
DROP TABLE IF EXISTS ORDERS;
DROP TABLE IF EXISTS ARTICLES_CATEGORIES;
DROP TABLE IF EXISTS CATEGORIES;
DROP TABLE IF EXISTS REVIEWS;
DROP TABLE IF EXISTS ARTICLES;
DROP TABLE IF EXISTS TICKETS;
DROP TABLE IF EXISTS USERS;

CREATE TABLE IF NOT EXISTS USERS(
    id INT PRIMARY KEY AUTO_INCREMENT,
    firstname VARCHAR(50) NOT NULL,
    first_lastname VARCHAR(50) NOT NULL,
    second_lastname VARCHAR(50) NOT NULL,
    document VARCHAR(9) NOT NULL,
    phone1 VARCHAR(9) NOT NULL,
    phone2 VARCHAR(9) NOT NULL,
    address VARCHAR(50) NOT NULL,
    location VARCHAR(30) NOT NULL,
    province VARCHAR(30) NOT NULL,
    country VARCHAR(30) NOT NULL,
    email VARCHAR(50) NOT NULL UNIQUE KEY,
    password VARCHAR(255) NOT NULL,
    rol INT NOT NULL DEFAULT 0, /* 0->user, 1->employment, 5->root */
    is_active INT NOT NULL DEFAULT 1
)ENGINE=INNODB CHARACTER SET latin1 COLLATE latin1_spanish_ci;

CREATE TABLE IF NOT EXISTS CATEGORIES(
    id INT PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(50) NOT NULL,
    description VARCHAR(50) NOT NULL,
    is_active INT NOT NULL DEFAULT 1,
    category_id INT NULL DEFAULT NULL,
    CONSTRAINT CATEGORIES_category_id FOREIGN KEY(category_id) REFERENCES CATEGORIES(id) ON UPDATE CASCADE ON DELETE CASCADE
)ENGINE=INNODB CHARACTER SET latin1 COLLATE latin1_spanish_ci;

CREATE TABLE IF NOT EXISTS ARTICLES(
    id INT PRIMARY KEY AUTO_INCREMENT,
    serial_number VARCHAR(50) NOT NULL,
    brand VARCHAR(255) NOT NULL,
    name VARCHAR(255) NOT NULL,
    description TEXT NOT NULL,
    especification TEXT NOT NULL,
    img_route VARCHAR(255) NOT NULL,
    price FLOAT NOT NULL,
    price_discount FLOAT NOT NULL DEFAULT 0,
    percentage_discount FLOAT NOT NULL DEFAULT 0,
    is_outlet INT NOT NULL DEFAULT 0,
    free_shipping INT NOT NULL DEFAULT 0,
    stock INT NOT NULL DEFAULT 0,
    warranty INT NOT NULL,
    return_days INT NOT NULL,
    visitor_counter INT NOT NULL DEFAULT 1,
    release_date DATE,
    is_active INT NOT NULL DEFAULT 1
)ENGINE=INNODB CHARACTER SET latin1 COLLATE latin1_spanish_ci;

CREATE TABLE IF NOT EXISTS ARTICLES_CATEGORIES(
    article_id INT NOT NULL,
    category_id INT NOT NULL,
    CONSTRAINT ARTICLES_CATEGORIES_article_id FOREIGN KEY(article_id) REFERENCES ARTICLES(id) ON UPDATE CASCADE ON DELETE CASCADE,
    CONSTRAINT ARTICLES_CATEGORIES_category_id FOREIGN KEY(category_id) REFERENCES CATEGORIES(id) ON UPDATE CASCADE ON DELETE CASCADE
)ENGINE=INNODB CHARACTER SET latin1 COLLATE latin1_spanish_ci;

CREATE TABLE IF NOT EXISTS REVIEWS(
    id INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(255) NOT NULL,
    description TEXT NOT NULL,
    rating INT NOT NULL DEFAULT 1,
    date DATETIME,
    article_id INT NULL DEFAULT NULL,
    user_id INT NULL DEFAULT NULL,
    CONSTRAINT REVIEWS_article_id FOREIGN KEY(article_id) REFERENCES ARTICLES(id) ON UPDATE CASCADE ON DELETE CASCADE,
    CONSTRAINT REVIEWS_user_id FOREIGN KEY(user_id) REFERENCES USERS(id) ON UPDATE CASCADE ON DELETE CASCADE
)ENGINE=INNODB CHARACTER SET latin1 COLLATE latin1_spanish_ci;

CREATE TABLE IF NOT EXISTS ORDERS(
    id INT PRIMARY KEY AUTO_INCREMENT,
    status INT NOT NULL DEFAULT 0,
    total_quantity FLOAT NOT NULL,
    total_price FLOAT NOT NULL,
    free_shipping INT NOT NULL DEFAULT 0,
    date DATETIME DEFAULT NULL,
    paid_method VARCHAR(50) DEFAULT NULL,
    user_id INT NULL DEFAULT NULL,
    CONSTRAINT ORDERS_user_id FOREIGN KEY(user_id) REFERENCES USERS(id) ON UPDATE CASCADE ON DELETE CASCADE
)ENGINE=INNODB CHARACTER SET latin1 COLLATE latin1_spanish_ci;

CREATE TABLE IF NOT EXISTS ORDERLINES(
    id INT PRIMARY KEY AUTO_INCREMENT,
    article_name VARCHAR(255) NOT NULL,
    article_img_route VARCHAR(255) NOT NULL,
    quantity FLOAT NOT NULL,
    price FLOAT NOT NULL,
    total_price FLOAT NOT NULL,
    free_shipping INT NOT NULL DEFAULT 0,
    article_id INT NOT NULL,
    order_id INT NULL DEFAULT NULL,
    CONSTRAINT ORDERLINES_article_id FOREIGN KEY(article_id) REFERENCES ARTICLES(id) ON UPDATE CASCADE ON DELETE CASCADE,
    CONSTRAINT ORDERLINES_order_id FOREIGN KEY(order_id) REFERENCES ORDERS(id) ON UPDATE CASCADE ON DELETE CASCADE
)ENGINE=INNODB CHARACTER SET latin1 COLLATE latin1_spanish_ci;

CREATE TABLE IF NOT EXISTS TICKETS(
    id INT PRIMARY KEY AUTO_INCREMENT,
    email VARCHAR(50) NOT NULL,
    message TEXT NOT NULL,
    date DATETIME,
    viewed INT NOT NULL DEFAULT 0,
    questioner INT NOT NULL,
    answerner INT DEFAULT NULL,
    CONSTRAINT TICKETS_questioner FOREIGN KEY(questioner) REFERENCES USERS(id) ON UPDATE CASCADE ON DELETE CASCADE,
    CONSTRAINT TICKETS_answerner FOREIGN KEY(answerner) REFERENCES USERS(id) ON UPDATE CASCADE ON DELETE CASCADE
)ENGINE=INNODB CHARACTER SET latin1 COLLATE latin1_spanish_ci;

